using System;using System.Linq;using System.Net;using System.Net.Mail;
public class Program{class LASTDIG{public static UInt64 Solve(UInt64 a, UInt64 b){if (b == 0){return 1;}if (b == 1){return a % 10;}UInt64 c = Solve(a, b / 2);c = (c * c) % 10;if (b % 2 == 1){c = (c * a) % 10;}return c;}}public static void Main(){int t;UInt64[] a;t = Convert.ToInt32(Console.ReadLine().Trim());for (int i = 0; i < t; i++){a = Console.ReadLine().Split(new char[] { ' ' }).Select(x => Convert.ToUInt64(x)).ToArray(); Console.WriteLine(LASTDIG.Solve(a[0], a[1]));}}}